@auto_docstring
class Qwen3Model(Qwen3PreTrainedModel):
    def __init__(self, config: Qwen3Config):
        super().__init__(config)
        self.padding_idx = config.pad_token_id
        self.vocab_size = config.vocab_size
        self.config = config

        # Single learnable vector for vocab_size=1 case
        self.embed_vector = nn.Parameter(torch.randn(config.hidden_size))
        self.layers = nn.ModuleList(
            [Qwen3DecoderLayer(config, layer_idx) for layer_idx in range(config.num_hidden_layers)]
        )
        self.norm = Qwen3RMSNorm(config.hidden_size, eps=config.rms_norm_eps)
        # Get dim from config (head_dim)
        # Get theta from rope_parameters if available, otherwise use default
        theta = config.rope_parameters.get("rope_theta", 10000.0) if hasattr(config, "rope_parameters") else 10000.0
        self.gradient_checkpointing = False
        self.has_sliding_layers = "sliding_attention" in self.config.layer_types

        # Initialize weights and apply final processing
        self.post_init()

    @check_model_inputs
    @auto_docstring
    def forward(
            self,
            input_ids: Optional[torch.LongTensor] = None,
            attention_mask: Optional[torch.Tensor] = None,
            position_ids: Optional[torch.LongTensor] = None,
            past_key_values: Optional[Cache] = None,
            inputs_embeds: Optional[torch.FloatTensor] = None,
            use_cache: Optional[bool] = None,
            cache_position: Optional[torch.LongTensor] = None,
            position_tensors: torch.Tensor = None,
            **kwargs: Unpack[TransformersKwargs],
    ) -> BaseModelOutputWithPast:
        if (input_ids is None) ^ (inputs_embeds is not None):
            raise ValueError("You must specify exactly one of input_ids or inputs_embeds")

        if inputs_embeds is None:
            # For vocab_size=1, expand the single embed vector to match batch and sequence dimensions
            batch_size, seq_len = input_ids.shape
            inputs_embeds = self.embed_vector.unsqueeze(0).unsqueeze(0).expand(batch_size, seq_len, -1)

        if use_cache and past_key_values is None:
            past_key_values = DynamicCache(config=self.config)

        if cache_position is None:
            past_seen_tokens = past_key_values.get_seq_length() if past_key_values is not None else 0
            cache_position = torch.arange(
                past_seen_tokens, past_seen_tokens + inputs_embeds.shape[1], device=inputs_embeds.device
            )

        if position_ids is None:
            position_ids = cache_position.unsqueeze(0)

        # It may already have been prepared by e.g. `generate`
        if not isinstance(causal_mask_mapping := attention_mask, dict):
            # Prepare mask arguments
            mask_kwargs = {
                "config": self.config,
                "input_embeds": inputs_embeds,
                "attention_mask": attention_mask,
                "cache_position": cache_position,
                "past_key_values": past_key_values,
                "position_ids": position_ids,
            }
            # Create the masks
            causal_mask_mapping = {
                "full_attention": create_causal_mask(**mask_kwargs),
            }
            # The sliding window alternating layers are not always activated depending on the config
            if self.has_sliding_layers:
                causal_mask_mapping["sliding_attention"] = create_sliding_window_causal_mask(**mask_kwargs)

        hidden_states = inputs_embeds
        rotary_pos_emb = self.rot_pos_emb(position_tensors)
        # Convert frequencies to cos and sin for RoPE
        position_embeddings = (rotary_pos_emb.cos(), rotary_pos_emb.sin())

        for decoder_layer in self.layers[: self.config.num_hidden_layers]:
            hidden_states = decoder_layer(
                hidden_states,
                attention_mask=causal_mask_mapping[decoder_layer.attention_type],
                position_embeddings=position_embeddings,
                position_ids=position_ids,
                past_key_values=past_key_values,
                use_cache=use_cache,
                cache_position=cache_position,
                **kwargs,
            )

        hidden_states = self.norm(hidden_states)
        return BaseModelOutputWithPast(
            last_hidden_state=hidden_states,
            past_key_values=past_key_values if use_cache else None,
        )

    def rot_pos_emb(self, position_tensors: torch.Tensor) -> torch.Tensor:
        """
        Generate rotary position embeddings for continuous 4D positions.

        Args:
            position_tensors: Tensor with shape (batch_size, seq_len, 4) containing
                             continuous 4D positions for each batch item

        Returns:
            torch.Tensor: Raw frequencies with shape (batch_size, seq_len, head_dim)
        """
        head_dim = getattr(self.config, "head_dim", self.config.hidden_size // self.config.num_attention_heads)
        theta = 10000

        device = position_tensors.device
        batch_size, seq_len = position_tensors.shape[:2]

        # Position tensors already have shape (batch_size, seq_len, 4)
        all_positions = position_tensors

        # Create frequency bands - for 4D positions, we use head_dim/8 unique frequencies
        # Each frequency will be used for a pair of dimensions (2D rotation)
        # Applied to all 4 position dimensions gives us head_dim/8 * 2 * 4 = head_dim
        freq_bands = 1.0 / (theta ** (torch.arange(0, head_dim // 4, 2, device=device).float() / head_dim))

        frequencies = []

        for d in range(4):  # For each of the 4 position dimensions
            # Get positions for this dimension: (batch_size, seq_len, 1)
            pos_d = all_positions[:, :, d:d + 1]

            # Apply frequency bands to this position dimension
            dim_frequencies = pos_d * freq_bands  # (batch_size, seq_len, num_freqs)

            # Duplicate each frequency to create pairs (matching RoPE paper)
            # Each frequency is used for both elements of a 2D rotation
            dim_frequencies = dim_frequencies.repeat_interleave(2, dim=-1)  # (batch_size, seq_len, head_dim//4)

            frequencies.append(dim_frequencies)

        # Concatenate frequencies from all 4 dimensions
        frequencies = torch.cat(frequencies, dim=-1)  # (batch_size, seq_len, head_dim)

        return frequencies


@auto_docstring
class Qwen3ForCausalLM(Qwen3PreTrainedModel, GenerationMixin):
    # No tied weights since we removed the embedding matrix
    _tp_plan = {"regression_head": "colwise_rep"}
    _pp_plan = {"regression_head": (["hidden_states"], ["logits"])}

    def __init__(self, config):
        super().__init__(config)
        self.model = Qwen3Model(config)
        self.vocab_size = config.vocab_size
        # Regression head that outputs RoPE-rotated representation (head_dim dimensions)
        head_dim = getattr(config, "head_dim", config.hidden_size // config.num_attention_heads)
        self.regression_head = nn.Linear(config.hidden_size, head_dim, bias=False)

        # Initialize weights and apply final processing
        self.post_init()

    @can_return_tuple
    @auto_docstring
    def forward(
            self,
            input_ids: Optional[torch.LongTensor] = None,
            attention_mask: Optional[torch.Tensor] = None,
            position_ids: Optional[torch.LongTensor] = None,
            past_key_values: Optional[Cache] = None,
            inputs_embeds: Optional[torch.FloatTensor] = None,
            labels: Optional[torch.LongTensor] = None,
            use_cache: Optional[bool] = None,
            cache_position: Optional[torch.LongTensor] = None,
            logits_to_keep: Union[int, torch.Tensor] = 0,
            position_tensors: torch.Tensor = None,
            **kwargs: Unpack[TransformersKwargs],
    ) -> CausalLMOutputWithPast:
        r"""
        labels (`torch.LongTensor` of shape `(batch_size, sequence_length)`, *optional*):
            Labels for computing the masked language modeling loss. Indices should either be in `[0, ...,
            config.vocab_size]` or -100 (see `input_ids` docstring). Tokens with indices set to `-100` are ignored
            (masked), the loss is only computed for the tokens with labels in `[0, ..., config.vocab_size]`.

        Example:

        ```python
        >>> from transformers import AutoTokenizer, Qwen3ForCausalLM

        >>> model = Qwen3ForCausalLM.from_pretrained("Qwen/Qwen3-8B")
        >>> tokenizer = AutoTokenizer.from_pretrained("Qwen/Qwen3-8B")

        >>> prompt = "Hey, are you conscious? Can you talk to me?"
        >>> inputs = tokenizer(prompt, return_tensors="pt")

        >>> # Generate
        >>> generate_ids = model.generate(inputs.input_ids, max_length=30)
        >>> tokenizer.batch_decode(generate_ids, skip_special_tokens=True, clean_up_tokenization_spaces=False)[0]
        "Hey, are you conscious? Can you talk to me?\nI'm not conscious, but I can talk to you."
        ```"""
        outputs: BaseModelOutputWithPast = self.model(
            input_ids=input_ids,
            attention_mask=attention_mask,
            position_ids=position_ids,
            past_key_values=past_key_values,
            inputs_embeds=inputs_embeds,
            use_cache=use_cache,
            cache_position=cache_position,
            position_tensors=position_tensors,
            **kwargs,
        )

        hidden_states = outputs.last_hidden_state
        # Only compute necessary logits, and do not upcast them to float if we are not computing the loss
        slice_indices = slice(-logits_to_keep, None) if isinstance(logits_to_keep, int) else logits_to_keep
        # Get RoPE-rotated representation outputs
        raw_outputs = self.regression_head(hidden_states[:, slice_indices, :])

        # Apply pairwise normalization to ensure unit vectors (as RoPE preserves norms)
        # Reshape to pairs: (batch, seq, head_dim) -> (batch, seq, head_dim//2, 2)
        batch_size, seq_len, head_dim = raw_outputs.shape
        pairs = raw_outputs.view(batch_size, seq_len, head_dim // 2, 2)

        # Normalize each pair to unit vectors
        pair_norms = torch.norm(pairs, dim=-1, keepdim=True)
        normalized_pairs = pairs / (pair_norms + 1e-8)  # Add small epsilon for numerical stability

        # Reshape back to original format
        outputs_4d = normalized_pairs.view(batch_size, seq_len, head_dim)

        loss = None
        if labels is not None:
            # Cosine similarity loss for RoPE-rotated representation
            import torch.nn.functional as F
            
            # Ensure labels have shape (batch_size, sequence_length, head_dim)
            if labels.dim() == 2:
                raise ValueError(
                    "For RoPE regression, labels should have shape (batch_size, sequence_length, head_dim)")
            
            # Reshape to pairs for pairwise cosine similarity
            pred_pairs = outputs_4d.view(batch_size, seq_len, head_dim // 2, 2)  # (batch, seq, 64, 2)
            target_pairs = labels.view(batch_size, seq_len, head_dim // 2, 2)    # (batch, seq, 64, 2)
            
            # Flatten pairs for computation
            pred_pairs_flat = pred_pairs.view(-1, 2)    # (batch*seq*64, 2)
            target_pairs_flat = target_pairs.view(-1, 2) # (batch*seq*64, 2)
            
            # Mask out padded positions (where labels == -100)
            mask = (target_pairs_flat != -100).all(dim=-1)  # (batch*seq*64,)
            
            if mask.any():
                pred_masked = pred_pairs_flat[mask]   # (valid_pairs, 2)
                target_masked = target_pairs_flat[mask] # (valid_pairs, 2)
                
                # Cosine similarity between corresponding 2D pairs
                cos_sim = F.cosine_similarity(pred_masked, target_masked, dim=-1)
                # Use arccos to ensure non-negative loss that can't cancel out
                # arccos(cos_sim) gives angle between vectors in [0, Ï€]
                loss = torch.arccos(torch.clamp(cos_sim, -1.0, 1.0)).mean()

                # Debug: log statistics
                if torch.rand(1).item() < 0.1:  # Log 1% of the time
                    print(f"Cosine sim stats: min={cos_sim.min():.3f}, max={cos_sim.max():.3f}, mean={cos_sim.mean():.3f}")
            else:
                loss = torch.tensor(0.0, device=outputs_4d.device)

        return CausalLMOutputWithPast(
            loss=loss,
            logits=outputs_4d,  # Now contains RoPE-rotated representation outputs
            past_key_values=outputs.past_key_values,
            hidden_states=outputs.hidden_states,
            attentions=outputs.attentions,
        )


class Qwen3ForQuestionAnswering(GenericForQuestionAnswering, Qwen3PreTrainedModel):
    base_model_prefix = "transformer"  # For BC, where `transformer` was used instead of `model`


__all__ = [
    "Qwen3ForCausalLM",
    "Qwen3ForQuestionAnswering",
    "Qwen3PreTrainedModel",
    "Qwen3Model"
]